def test_putrequest_invalid_method(self): conn = HTTPConnection('www.example.com') with self.assertRaises(ValueError): conn.putrequest('INVALID METHOD', '/')
def test_host_removes_trailing_dots(self): conn = HTTPConnection('www.example.com.') self.assertEqual(conn.host, 'www.example.com')
class TestHTTPSConnection(unittest.TestCase):
@patch('urllib3.connection.ssl') def test_set_cert_reqs(self, mock_ssl): conn = HTTPSConnection('www.example.com') conn.set_cert(cert_reqs='CERT_REQUIRED') self.assertEqual(conn.cert_reqs, 'CERT_REQUIRED')
@patch('urllib3.connection.ssl') def test_connect(self, mock_ssl): conn = HTTPSConnection('www.example.com') conn.connect() conn.sock.settimeout.assert_called_with(conn.timeout)
if __name__ == '__main__': unittest.main() ```
