from datetime import datetime, timedelta

def get_yesterday():
    presentday = datetime.now()
    yesterday = presentday - timedelta(1)
    return yesterday.strftime('%d-%m-%Y')

def get_today():
    presentday = datetime.now()
    return presentday.strftime('%d-%m-%Y')

def get_tomorrow():
    presentday = datetime.now()
    tomorrow = presentday + timedelta(1)
    return tomorrow.strftime('%d-%m-%Y')

class TestDateFunctions(unittest.TestCase):

    def test_get_yesterday(self):
        self.assertEqual(get_yesterday(), (datetime.now() - timedelta(1)).strftime('%d-%m-%Y'))

    def test_get_today(self):
        self.assertEqual(get_today(), datetime.now().strftime('%d-%m-%Y'))

    def test_get_tomorrow(self):
