```java
public class BubbleSortTest {

    @Test
    void testBubbleSortAlreadySorted() {
        int[] arr = {1, 2, 3, 4, 5};
        int[] expected = {1, 2, 3, 4, 5};
        bubbleSort(arr);
        Assertions.assertArrayEquals(expected, arr, "Array should remain sorted");
    }

    @Test
    void testBubbleSortWithDuplicates() {
        int[] arr = {5, 2, 3, 1, 2, 4};
        int[] expected = {1, 2, 2, 3, 4, 5};
        bubbleSort(arr);
        Assertions.assertArrayEquals(expected, arr, "Array should be sorted with duplicates in correct order");
    }

    @Test
    void testBubbleSortWithNegativeNumbers() {
        int[] arr = {-3, 0, 5, -1, 2};
        int[] expected = {-3, -1, 0, 2, 5};
        bubbleSort(arr);
        Assertions.assertArrayEquals(expected, arr, "Array should be sorted with negative numbers");
    }

    @Test
    void testBubbleSortEmptyArray() {
        int[] arr = {};
        int[] expected = {};
        bubbleSort(arr);
        Assertions.assertArrayEquals(expected, arr, "Empty array should remain unchanged");
    }

    // Import the bubbleSort method being tested
    public void bubbleSort(int[] arr) {
        int n = arr.length;
        for (int i = 0; i < n - 1; i++) {
            for (int j = 0; j < n - i - 1; j++) {
                if (arr[j] > arr[j + 1]) {
                    int temp = arr[j];
                    arr[j] = arr[j + 1];
                    arr[j + 1] = temp;
                }
            }
        }
    }
}
```

