```java
import org.junit.Assert;
import org.junit.Test;

public class BubbleSortTest {

    @Test
    public void testSortPositiveIntegers() {
        int[] arr = {5, 2, 8, 12, 1};
        bubbleSort(arr);
        Assert.assertArrayEquals(new int[]{1, 2, 5, 8, 12}, arr);
    }

    @Test
    public void testSortNegativeIntegers() {
        int[] arr = {-4, -9, -2, -7, -5};
        bubbleSort(arr);
        Assert.assertArrayEquals(new int[]{-9, -7, -5, -4, -2}, arr);
    }

    @Test
    public void testSortMixedIntegers() {
        int[] arr = {0, -3, 5, -1, 2};
        bubbleSort(arr);
        Assert.assertArrayEquals(new int[]{-3, -1, 0, 2, 5}, arr);
    }

    @Test
    public void testSortAlreadySortedArray() {
        int[] arr = {1, 2, 3, 4, 5};
        bubbleSort(arr);
        Assert.assertArrayEquals(new int[]{1, 2, 3, 4, 5}, arr);
    }

    @Test
    public void testSortEmptyArray() {
        int[] arr = {};
        bubbleSort(arr);
        Assert.assertArrayEquals(new int[]{}, arr);
    }

    @Test
    public void testSortArrayWithDuplicates() {
        int[] arr = {4, 2, 4, 3, 2, 1};
        bubbleSort(arr);
        Assert.assertArrayEquals(new int[]{1, 2, 2, 3, 4, 4}, arr);
    }

    // Method under test
    public void bubbleSort(int arr[]) {
        int n = arr.length;
        for (int i = 0; i < n - 1; i++) {
            for (int j = 0; j < n - i - 1; j++) {
                if (arr[j] > arr[j + 1]) {
                    int temp = arr[j];
                    arr[j] = arr[j + 1];
                    arr[j + 1] = temp;
                }
            }
        }
    }
}
```

