```java
import org.junit.Test;
import static org.junit.Assert.assertArrayEquals;

public class BubbleSortTest {
    @Test
    public void testBubbleSortSortedArray() {
        int[] arr = {1, 2, 3, 4, 5};
        int[] expected = {1, 2, 3, 4, 5};
        bubbleSort(arr);
        assertArrayEquals(expected, arr);
    }

    @Test
    public void testBubbleSortReversedArray() {
        int[] arr = {5, 4, 3, 2, 1};
        int[] expected = {1, 2, 3, 4, 5};
        bubbleSort(arr);
        assertArrayEquals(expected, arr);
    }

    @Test
    public void testBubbleSortRandomArray() {
        int[] arr = {3, 1, 4, 5, 2};
        int[] expected = {1, 2, 3, 4, 5};
        bubbleSort(arr);
        assertArrayEquals(expected, arr);
    }

    @Test
    public void testBubbleSortNullArray() {
        int[] arr = null;
        bubbleSort(arr);
        assertArrayEquals(null, arr);
    }

    @Test
    public void testBubbleSortEmptyArray() {
        int[] arr = {};
        int[] expected = {};
        bubbleSort(arr);
        assertArrayEquals(expected, arr);
    }

    private void bubbleSort(int[] arr) {
        if (arr == null) {
            return;
        }

        int n = arr.length;
        for (int i = 0; i < n - 1; i++) {
            for (int j = 0; j < n - i - 1; j++) {
                if (arr[j] > arr[j + 1]) {
                    int temp = arr[j];
                    arr[j] = arr[j + 1];
                    arr[j + 1] = temp;
                }
            }
        }
    }
}
```

